import os
import re


def scan_css_imports(directory):
    css_imports = []
    for root, dirs, files in os.walk(directory):
        for file in files:
            if file.endswith('.py'):
                with open(os.path.join(root, file), 'r') as f:
                    contents = f.read()
                matches = re.findall(r'import\s+\"(.+\.css)\"', contents)
                css_imports.extend(matches)
    return css_imports

def create_missing_files(file_paths):
    for file_path in file_paths:
        if not os.path.exists(file_path):
            with open(file_path, 'w'):
                pass

create_missing_files(scan_css_imports('/path/to/project/root'))
