import json

import requests


def fetch_issue_data(tracking_id):
    url = f"https://progress.sweep.dev/issues/{tracking_id}"
    response = requests.get(url)
    data = response.json()
    return data

def aggregate_issues(tracking_ids):
    all_issues = []
    for tracking_id in tracking_ids:
        issue_data = fetch_issue_data(tracking_id)
        all_issues.append(issue_data)
    all_issues_json = json.dumps(all_issues, indent=4)
    print(all_issues_json)
    with open("combined_issues.json", "w") as file:
        file.write(all_issues_json)

if __name__ == "__main__":
    example_tracking_ids = ["123", "456", "789"]
    aggregate_issues(example_tracking_ids)
